class BaseApi{getInner(n){const t=await window.fetch(n,{method:"GET"}),i=await t.json();if(!t.ok)throw new Error(i);return i}postInner(n,t){const i=await window.fetch(n,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)});return await i.json()}deleteInner(n){const t=await window.fetch(n,{method:"DELETE"});if(!t.ok)throw new Error(t);}}